{% set doc_cards = frappe.get_list(doc.doctype_card, fields = "name", filters = doc.filters) %}
<div class="row card-row">
    {% for doc_card in doc_cards %}
        <div class="col-xs-{{ (12 / doc.cards_per_page_width | int) | int }} doc-card">
            {{ frappe.render_template(doc.layout, {"doc_card": frappe.get_doc(doc.doctype_card, doc_card['name']), "doc": doc}) }}
        </div>
    {% endfor %}
</div>

<style>
    .doc-card{

        {% if doc.fixed_width == 1 %}
            width: {{ doc.card_width }}mm;
        {% endif %}


        {% if doc.fixed_height == 1 %}
            height: {{ doc.card_height}}mm;
        {% endif %}
        padding: {{doc.padding }}mm;
        border-style: {{doc.border}};
        border-width: {{ doc.border_width }};
        overflow: hidden;
        page-break-after: always;
        page-break-inside: avoid;
    }
    .card-row{
    margin:0;
    }
    @media print{
        .print-format{
            padding: 0.1in !important;
        }
    }
</style>
